import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Button } from 'react-native';
import { Formik } from 'formik';
import * as yup from "yup";
import { addBulletinList } from '../shared/bulletinList';

export default function RequestList({ route }) {
  const {name, phone, postal, unit} = route.params;

  function createRequestObject(favorDescription) {
    let randomId = Math.floor(Math.random() * 10000);

    return {
      id: randomId,
      name: name,
      phone: phone,
      postal: postal,
      unit: unit,
      items: [favorDescription]
    }
  }

  return (
    <View style={styles.container}>
      <Formik
        initialValues={{title: "", quantity: ""}}
        // validationSchema={registerSchema}
        onSubmit={
          // store data in db 
          (values) => { 
            let favor = createRequestObject(values);
            console.log(favor);
            addBulletinList(favor);
          }
        }
      >
        {({ handleChange, handleBlur, handleSubmit, values, touched, errors }) => (
          <View>
            {/* Description */}
            <TextInput 
              placeholder="Description"
              onChangeText={handleChange("title")}
              value={values.title}
              onBlur={handleBlur("title")}
            />
            <Text>{touched.title && errors.title}</Text>

            {/* Quantity */}
            <TextInput 
              placeholder="Quantity"
              onChangeText={handleChange("quantity")}
              value={values.quantity}
              keyboardType="numeric"
              onBlur={handleBlur("quantity")}
            />
            <Text>{touched.quantity && errors.quantity}</Text>

            <Button title="Create Favor" onPress={handleSubmit}/>
          </View>
        )}

      </Formik>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
