import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity, TextInput, Button } from 'react-native';
import { addBulletinList, getBulletinList } from '../shared/bulletinList';
import { getRequestList } from '../shared/requestList';
import { useIsFocused } from '@react-navigation/native';

export default function RequestList({navigation}) {
  let requestList = getRequestList();
  const isFocused = useIsFocused();
  return (
    <View style={styles.container}>
      {isFocused ? 
      (<FlatList 
        data={requestList}
        keyExtractor={item => item.id.toString()}
        renderItem={({item}) => (
          <View>
            <Text>{item.name}</Text>
            <Text>{item.phone}</Text>
            {item.items.map((favor) => (
              <View key={favor.title}>
                <Text>{favor.title}</Text>
                <Text>{favor.quantity}</Text>
              </View>
            ))}

            <TouchableOpacity 
              style={styles.button}
              onPress={() => {

              }}>
              <Text>Create New Favor</Text>
            </TouchableOpacity>
          </View>
        )}
      />)
      : <FlatList />}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
