let requestList = [
  {
    id: 1,
    name: "Amy",
    phone: "98765432",
    postal: "123456",
    unit: "05-05",
    items: [{
      title: "HL Milk 1L",
      quantity: "2"
    }]
  },
];

export function getRequestList() {
  return requestList;
}

export function addRequestList(favor) {
  requestList = [...requestList, favor];
}

export function removeRequestList(favor) {
  requestList.splice(requestList.indexOf(favor), 1);
}
