import React, { useEffect } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, FlatList } from 'react-native';
import { getAcceptList } from '../shared/acceptList';

export default function AcceptList({navigation}) {
  let acceptList = getAcceptList();
  navigation.addListener("focus", () => {
    acceptList = getAcceptList();
    return (
      <View style={styles.container}>
        <FlatList 
          data={acceptList}
          keyExtractor={item => item.id.toString()}
          renderItem={({item}) => (
            <View>
              <Text>{item.name}</Text>
              <Text>{item.phone}</Text>
              {item.items.map((favor) => (
                <View key={favor.title}>
                  <Text>{favor.title}</Text>
                  <Text>{favor.quantity}</Text>
                </View>
              ))}

              <TouchableOpacity 
                style={styles.button}
                onPress={() => {

                }}>
                <Text>Complete</Text>
              </TouchableOpacity>
            </View>
          )}
        />
      </View>
    );
  }) 

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
